import React, { Component } from 'react'
import { FormControl, FormGroup, Col, ControlLabel, Button, Well, Alert } from 'react-bootstrap'
import './App.css'
import axios from 'axios'


const req = axios.create({
  headers: {
    'X-Requested-With': 'XMLHttpRequest',
  }
});


class App extends Component {

  constructor(props){
    super(props)
    this.state = {
      value:'',
      response:'',
      error:'',
    }
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  handleChange(e) {
    this.setState({ value: e.target.value });
  }

  handleSubmit(){
    req.post('/echo', { echo:this.state.value }).then(response => {
      this.setState({
        error:'',
        response:response.data.echo
      })
    }).catch( error => {
      console.log(error)
      this.setState({
        error: error,
        response:''
      })
    })
  }

  render() {
    return (
      <div className="App">
        <div className="header">  
        <img src="logo.png" style={{margin:'12px'}} alt=""/></div>
        <div className="body">
          <FormGroup controlId="formHorizontalEmail">
            <Col componentClass={ControlLabel} sm={2}>
              Send
            </Col>
            <Col sm={10}>
              <FormControl type="email" placeholder="Email" onChange={this.handleChange}/>
            </Col>
          </FormGroup>

          <Button bsStyle="primary" onClick={this.handleSubmit}>Send</Button>
        </div>
        <Well>{this.state.response}</Well>
        {this.state.error? ( 
          <Alert bsStyle="danger">
            <strong>Ups!</strong><br/>
            {this.state.error.message}
          </Alert>
         ):''
        }
      </div>
    );
  }
}

export default App;
